INSERT INTO `section2lesson` (`lesson_code`, `section`, `descr`, `reference`, `url`) VALUES
('αλγ-πολ', '01', 'Εισαγωγή, Αλγόριθμοι και Υπολογιστικά Προβλήματα, Ανάλυση Αλγορίθμων, Ασυμπτωτικοί Συμβολισμοί', 'Dasgupta et al., Κεφ. 0 / Kleinberg, Tardos, 1.2, 2.1-2.2, 2.4-2.5 / Cormen et al., Κεφ. 1, 2 / Goodrich, Tamassia, 1.1, 1.2', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac1a.pdf, https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac1b.pdf'),
('αλγ-πολ', '02', 'Γραφήματα: Αναπαραστάσεις, Αναζήτηση κατά Πλάτος, Αναζήτηση σε Βάθος', 'Dasgupta et al., Κεφ. 3 / Kleinberg, Tardos, 3.1-3.3 / Cormen et al., 22.1, 22.2, 22.3 / Goodrich, Tamassia, 13.1-13.3', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac2.pdf'),
('αλγ-πολ', '03', 'Γραφήματα: Τοπολογική Ταξινόμηση, Συνδεδεμένες Συνιστώσες, Ισχυρά Συνδεδεμένες Συνιστώσες', 'Dasgupta et al., Κεφ. 3 / Kleinberg, Tardos, 3.5-3.6 / Cormen et al., 22.4, 22.5 / Goodrich, Tamassia, 13.4', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac3.pdf'),
('αλγ-πολ', '04', 'Διαίρει-και-Βασίλευε: Συγχωνευτική Ταξινόμηση, Αναδρομικές Σχέσεις, Διάμεσος και Επιλογή', 'Dasgupta et al., Κεφ. 2 / Kleinberg, Tardos, 5.1-5.2, 13.5 / Cormen et al., Κεφ. 2, 3, 4.1-4.3, 9.2 / Goodrich, Tamassia, 8.1, 11.1, 9.2', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac4-5-6.pdf'),
('αλγ-πολ', '05', 'Διαίρει-και-Βασίλευε: Πολλαπλασιασμός Ακεραίων, Πολλαπλασιασμός Πινάκων με τον Αλγόριθμο του Strassen, Ορθότητα με Χρήση Αναλλοίωτων Συνθηκών, Κάτω Φράγμα Ταξινόμησης', 'Dasgupta et al., Κεφ. 2 / Kleinberg, Tardos, 5.5 / Cormen et al., 28.2, 2.1, 8.1 / Goodrich, Tamassia, 11.2, 11.3, 1.2.3, 8.3, 9.1', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac4-5-6.pdf, https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac7.pdf\r\n '),
('αλγ-πολ', '06', 'Άπληστοι Αλγόριθμοι: Επιλογή Δραστηριοτήτων, Κλασματικό Σακίδιο, Κώδικες Huffman', 'Dasgupta et al., Κεφ. 5 / Kleinberg, Tardos, 4.1, 4.8 / Cormen et al., 16.1, 16.2, 16.3 / Goodrich, Tamassia, 10.1, 10.2, 10.3', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac8-9.pdf'),
('αλγ-πολ', '07', 'Άπληστοι Αλγόριθμοι σε Γραφήματα: Ελάχιστο Συνδετικό Δέντρο: Αλγόριθμοι του Kruskal και του Prim', 'Dasgupta et al., Κεφ. 5 / Kleinberg, Tardos, 4.5 / Cormen et al., 23.2 / Goodrich, Tamassia, 15.1-15.3', 'https://eclass.uop.gr/modules/document/file.php/CST355/%CE%94%CE%B9%CE%B1%CF%86%CE%AC%CE%BD%CE%B5%CE%B9%CE%B5%CF%82%20%CE%BA%CE%B1%CE%B9%20%CE%A3%CE%B7%CE%BC%CE%B5%CE%B9%CF%8E%CF%83%CE%B5%CE%B9%CF%82/ac10.pdf'),
